//package albert.codeforces.restaurant597b; import java.util.Comparator;import java.util.PriorityQueue;import java.util.Scanner; public class Solution {    public static void main(String[] args) {        Scanner scan = new Scanner(System.in);        int n = scan.nextInt();        PriorityQueue<int[]> q = new PriorityQueue<>(Comparator.comparingInt(o -> o[1]));        while (n-- > 0) {            q.offer(new int[] {scan.nextInt(), scan.nextInt()});        }        int ans = 0;        int last = 0;        while (!q.isEmpty()) {            int[] cur = q.poll();            if (last < cur[0]) {                ans++;                last = cur[1];            }        }        System.out.println(ans);    }}
