import java.io.BufferedReader;import java.io.FileNotFoundException;import java.io.FileReader;import java.io.IOException;import java.io.InputStream;import java.io.InputStreamReader;import java.io.PrintWriter;import java.util.*;public class Main{ 	public static void main(String[] args)  throws IOException, InterruptedException{		PrintWriter pw = new PrintWriter(System.out);		Scanner sc = new Scanner (System.in);		int n = sc.nextInt();		int m = sc.nextInt();		TreeMap<Integer, Integer> tm = new TreeMap<>();		Integer [] arr = new Integer [n]; 		for(int i = 0 ; i < n ; i ++ ) {			arr[i] = sc.nextInt();					}		Arrays.sort(arr);		for (int i = 0 ; i < n ; i ++ ) {			tm.put(arr[i], i+1) ;					}		for (int i = 0 ; i < m ; i ++ ) {			int x = sc.nextInt();			if (tm.lowerKey(x+1) == null ) pw.print(0 + " " ) ;			else {				int c  = tm.lowerKey(x+1);				pw.print(tm.get(c)+ " " );			}											}		pw.close ();	}	static class Scanner {		StringTokenizer st;		BufferedReader br; 		public Scanner(InputStream s) {			br = new BufferedReader(new InputStreamReader(s));		} 		public Scanner(String file) throws FileNotFoundException {			br = new BufferedReader(new FileReader(file));		} 		public String next() throws IOException {			while (st == null || !st.hasMoreTokens())				st = new StringTokenizer(br.readLine());			return st.nextToken();		} 		public int nextInt() throws IOException {			return Integer.parseInt(next());		}		public long nextLong() throws IOException {			return Long.parseLong(next());		} 		public String nextLine() throws IOException {			return br.readLine();		}		public double nextDouble() throws IOException {			return Double.parseDouble(next());		} 		public boolean ready() throws IOException {			return br.ready();		} 	}  }			  	 				 		  	 		  		  		 		
