//package Coding; import java.io.*;import java.math.BigInteger;import java.nio.Buffer;import java.util.*; public class CodeForces {    public static void main(String[] args) throws FileNotFoundException {        Scanner in = new Scanner(System.in);        int xLen = in.nextInt();        int xBase = in.nextInt();        long xVal = 0;         for (int i = xLen - 1; i >= 0; --i) {            xVal += (long) Math.pow(xBase, i) * in.nextInt();        }         int yLen = in.nextInt();        int yBase = in.nextInt();        long yVal = 0;         for (int i = yLen - 1; i >= 0; --i) {            yVal += (long) Math.pow(yBase, i) * in.nextInt();        }        if (xVal > yVal) {            System.out.println(">");        } else if (xVal < yVal) {            System.out.println("<");        } else {            System.out.println("=");        }       }  }          
