//package com; import java.util.Arrays;import java.util.Comparator;import java.util.Scanner; /** * 1 9 * 3 1 * 6 1 * 7 4 */ public class CodeforcesContest {    static int max = 1_000_003;     private static int sport(int[] a, int n) {        int[] dp = new int[max];        int ans = 1;        if (a[0] > 0) {            dp[0] = 1;        }        for (int i = 1; i < max; i++) {            if (a[i] > 0) {                if (i - a[i] - 1 >= 0) {                    dp[i] = dp[i - a[i] - 1] + 1;                }else{                    dp[i]=1;                }            } else {                dp[i] = dp[i - 1];            }            ans = Math.max(ans, dp[i]);        }        return n - ans;    }     public static void main(String[] args) {        Scanner sc = new Scanner(System.in);        int n = sc.nextInt();        int[] a = new int[max];        for (int i = 0; i < n; i++) {            a[sc.nextInt()] = sc.nextInt();        }        System.out.println(sport(a,n));     }}
