import java.io.*;import java.util.*;public class MyClass {    public static void main(String args[]) throws IOException{        BufferedReader br = new BufferedReader(new InputStreamReader(System.in));        String a = br.readLine();        String b = br.readLine();        String rs = "";        for(int i=0;i<a.length();i++){            int ch1 = a.charAt(i)-'0';            int ch2 = b.charAt(i)-'0';            rs+=(ch1^ch2);        }        System.out.println(rs);    }}
