//package albert.codeforces.newyearandoldpropery611b; import java.util.Scanner; /** * https://codeforces.com/problemset/problem/611/B */public class Solution {    public static void main(String[] args) {        Scanner in = new Scanner(System.in);        long l = in.nextLong();        long r = in.nextLong();        int lBits = bits(l);        int rBits = bits(r);        int ans = 0;        for (int i = lBits; i <= rBits; i++) {            long start = (1L << i) - 1;            for (int j = 0; j < i - 1; j++) {                long w = start ^ (1L << j);                if (w >= l && w <= r) {                    ans++;                }            }        }        System.out.println(ans);    }     private static int bits(long l) {        int b = 0;        while (l > 0) {            l = l >> 1;            b++;        }        return b;    }}
