//package albert.codeforces.chocolate617b; import java.util.ArrayList;import java.util.List;import java.util.Scanner; public class Solution {    public static void main(String[] args) {        Scanner scan = new Scanner(System.in);        int n = scan.nextInt();        List<Integer> list = new ArrayList<>();        for (int i = 0; i < n; i++) {            int a = scan.nextInt();            if (a == 1) {                list.add(i);            }        }        if (list.isEmpty()) {            System.out.println(0);        } else {            long ans = 1;            int s = list.size();            for (int i = 1; i < s; i++) {                int gap = list.get(i) - list.get(i - 1);                ans = ans * gap;            }            System.out.println(ans);        }    }}
