//package albert.codeforces.astudentsdream62a; import java.util.Scanner; public class Solution {    public static void main(String[] args) {        Scanner scan = new Scanner(System.in);        int gl = scan.nextInt();        int gr = scan.nextInt();         int bl = scan.nextInt();        int br = scan.nextInt();         boolean good = can(gl, br) || can(gr, bl);        System.out.println(good ? "YES" : "NO");    }     private static boolean can(int gr, int bl) {        return bl >= gr - 1 && bl <= gr * 2 + 2;    }}
