//stan hu taoimport static java.lang.Math.max;import static java.lang.Math.min;import static java.lang.Math.abs;import java.util.*;import java.io.*;import java.math.*;Â public class x62B{    public static void main(String hi[]) throws Exception    {        BufferedReader infile = new BufferedReader(new InputStreamReader(System.in));        StringTokenizer st = new StringTokenizer(infile.readLine());        int T = Integer.parseInt(st.nextToken());        int N = Integer.parseInt(st.nextToken());        char[] arr = infile.readLine().toCharArray();        TreeSet<Integer>[] locs = new TreeSet[26];        for(int i=0; i < 26; i++)            locs[i] = new TreeSet<Integer>();        for(int i=0; i < N; i++)            locs[arr[i]-'a'].add(i);        StringBuilder sb = new StringBuilder();        while(T-->0)        {            arr = infile.readLine().toCharArray();            long cost = 0L;            for(int i=0; i < arr.length; i++)            {                int temp = arr.length;                if(locs[arr[i]-'a'].size() > 0)                {                    temp = Integer.MAX_VALUE;                    if(locs[arr[i]-'a'].first() <= i)                        temp = i-locs[arr[i]-'a'].floor(i);                    if(locs[arr[i]-'a'].last() >= i)                        temp = min(temp, locs[arr[i]-'a'].ceiling(i)-i);                }                cost += temp;            }            sb.append(cost+"\n");        }        System.out.print(sb);    }}
