# CODED BY SUMIT KUMAR PRAJAPATI  import sysimport math MD=int(1e9+7)MX=int(1e6+2)pi=3.1415926535897932  def intinp():    return int(input()) def arinput():    return list(map(int,input().strip().split())) def binary(N):    l=1    r=int(1e8)    ans=1    while l<=r:        mid=(l+r)>>1        if ((mid*(mid+1))>>1) > N:            r=mid-1        else:            ans=mid            l=mid+1    return ans def solve():    N=intinp()    n=binary(N)        ans=N%((n*(n+1))>>1)    if ans==0:        print(n)    else:        print(ans)  if __name__=='__main__':         solve()     
