import java.util.*;import java.lang.*;// import java.math.*;import java.io.*;public class Main{        public static void main (String[] args) throws java.lang.Exception	{        // your code goes here		try{            BufferedReader br=new BufferedReader(new InputStreamReader(System.in));            PrintWriter ot=new PrintWriter(System.out);            int t=1;//Integer.parseInt(br.readLine().trim());            // int total=0;            while(t-->0)            {                long n=Long.parseLong(br.readLine().trim());                long a=Long.parseLong(br.readLine().trim());                long b=Long.parseLong(br.readLine().trim());                long c=Long.parseLong(br.readLine().trim());                ot.println(find(n,a,b,c));            }            ot.close();            br.close();                    } catch(Exception e){            e.printStackTrace();            return;        }    }    public static long find(long n,long a,long b,long c){        long glasscost=b-c;        long count=0;        if(glasscost<a)        {            if(n>=b)            count+=((n-b)/(b-c))+1;            else            count=0;            count+=(n-count*(b-c))/a;        }        else        {            count+=(n/a);        }        return count;    }}    
