#   When in doubt, collections.Counter() #   input()#   int(input())#   input().split()#   list(map(int, input().split()))#   for tt in range(t): import mathimport collectionsimport bisect def arrPrint(a):    return " ".join([str(i) for i in a]) def gridPrint(a):    return "\n".join([" ".join([str(j) for j in a[i]]) for i in range(len(a))]) def isPalindrome(s):    for i in range(len(s)//2):        if not s[i] == s[-i-1]:            return False    return True def primeCheck(n):    for i in range(2, int(math.sqrt(n))+1):        if n%i == 0:            return False    return True def solve(s):    ans = 0    for i in range(len(s)):        if int(s[i])%4 == 0:            ans += 1            #print(s[i])    for i in range(len(s)-1):        if int(s[i:i+2])%4 == 0:            ans += i+1            #print(s[i:i+2])    return ans s = input()print(solve(s))
