import java.util.*;import java.util.stream.*; public class Solution {	public static void main(String[] args) {		Scanner scan = new Scanner(System.in);		List<String> rat = new ArrayList<>();		List<String> womanChild = new ArrayList<>();		List<String> man = new ArrayList<>();		String captain = "";		int n = scan.nextInt();		scan.nextLine();		for(int i = 0; i < n; i++) {			String[] info = scan.nextLine().split(" ");			if(info[1].equals("rat")) {				rat.add(info[0]);			}			if(info[1].equals("woman") || info[1].equals("child")) {				womanChild.add(info[0]);			}			if(info[1].equals("man")) {				man.add(info[0]);			}			if(info[1].equals("captain")) {				captain = info[0];			}		}				for(String elem : rat) {			System.out.println(elem);		}		for(String elem : womanChild) {			System.out.println(elem);		}		for(String elem : man) {			System.out.println(elem);		}		System.out.println(captain);	}}
