import java.util.*;public class square{    public static void main(String ar[])    {        Scanner sc=new Scanner(System.in);        int n=sc.nextInt();        String d=sc.next();       char[] dir=d.toCharArray();       int[] jumps=new int[n];       for(int j=0;j<n;j++)jumps[j]=sc.nextInt();       HashSet<Integer> visited=new HashSet();       visited.add(0);       int curr=0;       while(true)       {if(dir[curr]=='<') curr=curr-jumps[curr];else if(dir[curr]=='>') curr=curr+jumps[curr];if(curr<0 || curr>=n) {System.out.println("FINITE");return;}else if(visited.contains(curr)){System.out.println("INFINITE");return;}visited.add(curr);       }}}
