//stan hu taoimport static java.lang.Math.max;import static java.lang.Math.min;import static java.lang.Math.abs;import java.util.*;import java.io.*;import java.math.*;Â public class x65A{    public static void main(String hi[]) throws Exception    {        BufferedReader infile = new BufferedReader(new InputStreamReader(System.in));        StringTokenizer st = null;        int[] arr = readArr(6, infile, st);        long a = arr[0]*arr[2]*arr[4];        long b = arr[1]*arr[3]*arr[5];        if(a < b)            System.out.println("Ron");        else if(a == b && a == 0)        {            String res = "Hermione";            if(arr[2] == 0 && arr[3] > 0)                res = "Ron";            else if(min(arr[2], arr[3]) > 0 && arr[0] == 0 && arr[1] > 0)                res = "Ron";            System.out.println(res);        }        else            System.out.println("Hermione");    }    public static int[] readArr(int N, BufferedReader infile, StringTokenizer st) throws Exception    {        int[] arr = new int[N];        st = new StringTokenizer(infile.readLine());        for(int i=0; i < N; i++)            arr[i] = Integer.parseInt(st.nextToken());        return arr;    }}
