//c++ is being mean :( import java.util.*;import java.io.*;  public class Main{	public static void main(String args[]){		Scanner kb=new Scanner(System.in);		int n=kb.nextInt();		HashMap<Long,Integer> mx = new HashMap<Long,Integer>();		HashMap<Long,Integer> my = new HashMap<Long,Integer>();		HashMap<Long,Integer> p = new HashMap<Long,Integer>();		long sum=0;		for(int i =0;i<n;i++){			long x=kb.nextLong();			long y=kb.nextLong();			//System.out.println(x+" "+y); 			//System.out.println(x+" "+mx.containsKey(x));			if(!mx.containsKey(x)){				mx.put(x,1);			}			else{				sum+=mx.get(x);				mx.put(x,mx.get(x)+1);				//System.out.println("added for x");			}			//System.out.println(mx);						//System.out.println(y+" "+my.containsKey(y));			if(!my.containsKey(y)){				my.put(y,1);			}			else{				sum+=my.get(y);				my.put(y,my.get(y)+1);				//System.out.println("added for y");			}			//System.out.println(my);			long coor = (x+1000000001)*2000000001+y+1000000001; 			if(!p.containsKey(coor)){				p.put(coor,1);			}			else{				sum-=p.get(coor);				p.put(coor,p.get(coor)+1);			}			//System.out.println(sum);		}		System.out.println(sum);	}}	 	 					 	 		   							 		   	
