import java.util.Scanner;import java.util.Arrays;import java.util.HashSet;public class Main{    public static void main(String[] args)    {        Scanner puttingvaluesinside = new Scanner(System.in);        int arr[][] = new int[1001][1001];        int t = puttingvaluesinside.nextInt();        while (t-->0)        {            int temp = puttingvaluesinside.nextInt();            for(int i=0;i<arr.length;i++)            {                if(arr[i][temp]==0)                {                    arr[i][temp]=1;                    break;                }            }        }        int ans = 0;        for(int i=0;i<arr.length;i++)        {            int count = 0;            for(int j=0;j<arr.length;j++)            {                if(arr[i][j]==1)                    count++;            }            if(count==0)                break;            ans+=count-1;        }        System.out.println(ans);    }}
