import java.util.*;public class A{public static void main(String[] args){    Scanner scan = new Scanner(System.in);        long n = scan.nextLong();        long[] ans = new long[36];        ans[0] = 1; ans[1] = 2; ans[2] = 4;        for(int i =3;i<36;i++){            ans[i] = ans[i-1]*2;            if(ans[i] ==8192){                ans[i] = 8092;            }        }        System.out.println(ans[(int)(n)]);    }}
