# coding: utf-8 from functools import reduce def gcd (x, y):  while y:    x, y = y, x % y      return x  def lcm (x, y):  return x * y // gcd(x, y)  n = int(input())m = list(map(int, input().split()))r = list(map(int, input().split()))g = reduce(lambda x, y: lcm(x, y), m)count = 0 for i in range(g):  for j in range(n):    if i % m[j] == r[j]:      count += 1      break print(count / g)
