// "static void main" must be defined in a public class. import java.util.*; public class Main {        public static double [][] p;        public static void pour(int i, int j, int m, int n, double wine){        if(i < 0 || j < 0 || i >= m || j >= n) return ;        if(p[i][j] + wine <= 1.0) {p[i][j] += wine; return;}        else {            double amount = 0.5*((p[i][j] + wine) - 1.0);            pour(i+1, j, m, n, amount);            pour(i+1, j+1, m, n, amount);        }    }        public static int print(int n){        int count = 0;        for(int i=0; i<n; i++){            for(int j=0; j<n; j++){                // System.out.print(p[i][j] + " ");                if(p[i][j] >= 1.0)count ++;            }            // System.out.println();        }        return count;    }            public static int solve(int n, int t){        p = new double [n][n];        while (t-- > 0){            pour(0,0,n,n,1.0);        }        return print(n);    }        public static void main(String[] args) {        Scanner sc = new Scanner(System.in);        int n = sc.nextInt();        int t = sc.nextInt();        System.out.println(solve(n, t));    }}
