// Problem : B. Bear and Tower of Cubes// Contest : Codeforces - Codeforces Round #356 (Div. 1)// URL : https://codeforces.com/contest/679/problem/B// Memory Limit : 256 MB// Time Limit : 2000 ms// Powered by CP Editor (https://github.com/cpeditor/cpeditor) import java.io.*;import java.util.*; public class a implements Runnable{	    public static void main(String[] args) {        new Thread(null, new a(), "process", 1<<26).start();    }	public void run() {		FastReader scan = new FastReader();        PrintWriter out = new PrintWriter(new OutputStreamWriter(System.out));		//PrintWriter out = new PrintWriter("file.out");		Task solver = new Task();		//int t = scan.nextInt();		int t = 1;		for(int i = 1; i <= t; i++) solver.solve(i, scan, out);		out.close();	} 	static class Task {		static final int inf = Integer.MAX_VALUE; 		long m;		long[] arr = new long[100000];		long max = 0;		long max2 = 0;		public void solve(int testNumber, FastReader sc, PrintWriter pw) {			//CHECK FOR QUICKSORT TLE			//***********************//			//CHECK FOR INT OVERFLOW			//***********************//			for(int i = 1; i <= 100000; i++)				arr[i - 1] = 1l * i * i * i;			m = sc.nextLong();			check(m, 0, 0);			pw.println(max + " "+ max2);					}		long cube(long l){return l * l * l;}		void check(long m, long bloc, long alr){			if(m == 0) {				if(max == bloc) {					max = bloc;					max2 = Math.max(max2, alr);				}				if(max < bloc) {					max = bloc;					max2 = alr;				}				return;			}			long next = 1;			while(cube(next + 1) <= m) {				next ++;			}			check(m - cube(next), bloc + 1, alr + cube(next));			if(next >= 1){				check(cube(next) - 1 - cube(next - 1), bloc + 1, alr + cube(next - 1));			}		}	}	static long binpow(long a, long b, long m) {		a %= m;		long res = 1;		while (b > 0) {			if ((b & 1) == 1)				res = res * a % m;			a = a * a % m;			b >>= 1;		}		return res;	}	static void sort(int[] x){		shuffle(x);		Arrays.sort(x);	}	static void sort(long[] x){		shuffle(x);		Arrays.sort(x);	}	static class tup implements Comparable<tup>{		int a, b;		tup(int a,int b){			this.a=a;			this.b=b;		}		@Override		public int compareTo(tup o){			return Integer.compare(o.b,b);		}	}	static void shuffle(int[] a) {		Random get = new Random();		for (int i = 0; i < a.length; i++) {			int r = get.nextInt(a.length);			int temp = a[i];			a[i] = a[r];			a[r] = temp;		}	} 	static void shuffle(long[] a) {		Random get = new Random();		for (int i = 0; i < a.length; i++) {			int r = get.nextInt(a.length);			long temp = a[i];			a[i] = a[r];			a[r] = temp;		}	} 	static class FastReader {		BufferedReader br;		StringTokenizer st; 		public FastReader() {			br = new BufferedReader(new InputStreamReader(System.in));		} 		public FastReader(String s) throws FileNotFoundException {			br = new BufferedReader(new FileReader(new File(s)));		} 		String next() {			while (st == null || !st.hasMoreElements()) {				try {					st = new StringTokenizer(br.readLine());				} catch (IOException e) {					e.printStackTrace();				}			}			return st.nextToken();		} 		int nextInt() {			return Integer.parseInt(next());		} 		long nextLong() {			return Long.parseLong(next());		} 		double nextDouble() {			return Double.parseDouble(next());		} 		String nextLine() {			String str = "";			try {				str = br.readLine();			} catch (IOException e) {				e.printStackTrace();			}			return str;		}	} }
