#include <bits/stdc++.h>using namespace std;#define int long long#define debug(x) cout<<"[" << #x << " is: " << x << "] "<<endl;#define endl '\n'int mod = 1000000007;int INF = 1e18;long double PI = 3.14159265359; int to10(string s){    int ans = 0;    for(int i=0;i<s.size();i++) {        ans = ans * 7 + s[i] - '0';    }     return ans;} int maxdigits(int k){    int ans = 0;    if(k==0)        return 1;    while(k)    {        ans++;        k/=7;    }    return ans;} int tomask(string s){    int n = 0;    for(int i=0;i<s.size();i++)        n += (1ll<<(s[i]-'0'));    return n;} int n,m;map<int,int> a, b;void dfs(string s, int k, int fill){    if(k==0)    {        string asd = s;        if(fill==0 && to10(s) < n)            a[tomask(s)]++;        else if(fill==1 && to10(s) < m)            b[tomask(s)]++;        return;    }    bool used[7] = { false };    for(int i=0;i<s.size();i++)        used[s[i]-'0'] = true;    for(int i=0;i<7;i++)    {        if(!used[i])        {            string str = s;            str += char(i+'0');            dfs(str, k-1, fill);        }    }} int32_t main() {    ios::sync_with_stdio(false);    cin.tie(nullptr);      cin>>n>>m;    dfs("", maxdigits(n-1), 0);    dfs("", maxdigits(m-1), 1);    int ans = 0;    for(auto it1 : a)    {        for(auto it2 : b)        {            bool isok = true;            for(int i=0;i<11 && isok;i++)            {                if(((1ll<<i) & it1.first) && ((1ll<<i) & it2.first))                    isok = false;            }            if(isok)                ans += it1.second * it2.second;        }    }     cout<<ans<<endl;      return 0;}
