import java.io.*;import java.util.*;public class MyClass {    public static void main(String args[]) throws IOException{        BufferedReader br = new BufferedReader(new InputStreamReader(System.in));        int n = Integer.parseInt(br.readLine());        int arr[] = new int[3];        while(n-->0){            String s[] = br.readLine().split(" ");            for(int i=0;i<3;i++){                arr[i]+=Integer.parseInt(s[i]);            }        }        for(int i=0;i<3;i++){            if(arr[i]!=0){                System.out.println("NO");                return;            }        }        System.out.println("YES");    }}
