import java.util.Scanner;public class YoungPhysicist{    public static void main(String[] args)    {        Scanner in = new Scanner(System.in);        int n,x,y,z,Sum_X = 0,Sum_Y = 0,Sum_Z = 0;        n = in.nextInt();        for(int i = 0 ; i<n; i++)        {            x = in.nextInt();            y = in.nextInt();            z = in.nextInt();            Sum_X = Sum_X + x;            Sum_Y = Sum_Y + y;            Sum_Z = Sum_Z + z;        }        if(Sum_X == 0 && Sum_Y == 0 && Sum_Z == 0)            System.out.println("YES");        else            System.out.println("NO");    }  }
