#include<iostream>#include<vector>#include<string>#include<unordered_map>#include<unordered_set>#include<cmath>#include<algorithm>#include<climits>#include<map>#include<cstdlib>#include<queue>#include<functional>#include<iomanip>#include<set>using namespace std;#define ll long long    void solve(){        int n;       cin>>n;        vector<int>r(n+1,0);        for(int i =1; i<=n;i++){           cin>>r[i];       }        vector<vector<int>>f(n+1,vector<int>(3,0));        // 0 => contest, 1 => gym, 2 => rest       for(int i =1; i<=n;i++){           f[i][2] = max(f[i-1][1],max(f[i-1][0],f[i-1][2]));            if(r[i] == 2 || r[i] == 3){                f[i][1] = max(f[i-1][0]+1,f[i-1][2]+1);           }else{               f[i][1] = f[i-1][1];           }            if(r[i] == 1 || r[i] == 3){               f[i][0] = max(f[i-1][1]+1,f[i-1][2]+1);           }else{               f[i][0] = f[i-1][0];           }       }        cout<<n-max(f[n][0],max(f[n][1],f[n][2]))<<endl;}    int main(){         int t = 1;        while(t--){            solve();        }    return 0;}
