import java.util.Scanner;public class Cf {       public static void main(String[] args) {      Scanner input = new Scanner(System.in);int n = input.nextInt();String S= input.next();int array[]= new int [n];for(int i =0 ;i<n ; i++){array [i]= input.nextInt();}boolean collision=false;int distance=1000000000;for(int i =0 ;i<n-1 ; i++){if (S.charAt(i)=='R'&&S.charAt(i+1)=='L'){   collision=true;   int temp=(array[i+1]-array[i]);   if(temp<distance)       distance=temp;}}if(collision) System.out.println(distance/2);        else System.out.println("-1");        }  }
