// practice with rainboyimport java.io.*;import java.util.*; public class CF70B extends PrintWriter {	CF70B() { super(System.out, true); }	Scanner sc = new Scanner(System.in);	public static void main(String[] $) {		CF70B o = new CF70B(); o.main(); o.flush();	} 	void main() {		int n = Integer.parseInt(sc.nextLine());		byte[] cc = sc.nextLine().getBytes();		int m = cc.length;		int[] jj = new int[n];		int cnt = 0;		for (int j = 0; j < m; j++)			if (cc[j] == '.' || cc[j] == '?' || cc[j] == '!')				jj[cnt++] = j;		for (int h = 0, j = -1; h < cnt; j = jj[h++] + 1)			if (jj[h] - j > n) {				println("Impossible");				return;			}		int splits = 0;		for (int h = 0, j = -1; h < cnt; h++)			if (jj[h] - j > n) {				splits++;				j = jj[h - 1] + 1;			}		println(splits + 1);	}}
