//package codechef;Â import java.util.*;import java.io.*;public class CodeChef{    public static long arr_max(long arr[])    {        long max = 0;        for(int i = 0; i<arr.length; i++)        {            if(arr[i]>max) max = arr[i];        }        return max;    }    public static int gcd(int a,int b)    {        if(b==0) return a;        return gcd(b,a%b);    }    public static int fact(int n)    {        int fact = 1;        for(int i = 1; i<=n; i++) fact*=i;        return fact;    }    public static boolean isPrime(int n)    {        if(n==2) return true;        else if(n%2==0) return false;        for(int i = 2; i<=Math.sqrt(n); i++)        {            if(n%i==0)            {                return false;            }        }        return true;    }    public static void main(String[] args)throws IOException    {        BufferedReader br = new BufferedReader(new InputStreamReader(System.in));        int n = Integer.parseInt(br.readLine());        String s[] = br.readLine().split(" ");        int sum = 0;        int temp[] = new int[n];        int arr[] = new int[n];        for(int i = 0; i<n; i++)         {            arr[i] = Integer.parseInt(s[i]);            sum+=arr[i];        }        sum = sum / (n/2);        for(int i = 0; i<n/2;i++)        {            for(int j = 0; j<n&&i<n/2; j++)            {                if(temp[j]==0)                {                    temp[j]++;                    for(int k = 0; k<n; k++)                    {                        if(temp[k]==0&&arr[j]+arr[k]==sum)                        {                            System.out.println((j+1)+" "+(k+1));                            temp[k]++;                            i++;                            break;                        }                    }                }            }        }            }}
