import java.util.*;public class Main{    public static void main(String args[])    {        Scanner sc = new Scanner(System.in);        int n = sc.nextInt();        int m=0,c=0,a,b;        for(int i=0;i<n;i++)        {            a = sc.nextInt();            b = sc.nextInt();            if(a>b) m++;            else if(b>a) c++;        }        if(m>c)        {            System.out.println("Mishka");        }        else if(m<c)        {            System.out.println("Chris");        }        else        {            System.out.println("Friendship is magic!^^");        }    }}
