import java.util.*;public class Main {    public static void main(String[] args) {        Scanner sc = new Scanner(System.in);        int n = sc.nextInt();        int ar[] = new int[n];        for(int t =0;t<n;t++){            ar[t] = sc.nextInt();        }        Arrays.sort(ar);        int zz = ar[ar.length-1];        int y[] = new int[zz+1];        for(int u:ar){            y[u]++;        }        for(int o = 1;o<zz;o++){            y[o] = y[o] + y[o-1];        }        int q = sc.nextInt();        for(int r = 0;r<q;r++){            int high = sc.nextInt();            if(high>zz-1){                System.out.println(n);            }            else{                System.out.println(y[high]);            }        }                    }}
