import java.util.*;//referenced from the user trogdorpublic class Main{    public static void main(String gg[]) {        Scanner sc = new Scanner(System.in);        int n = sc.nextInt();        int a = sc.nextInt();        int[] checkPoints = new int[n];        for (int i = 0; i < n; i++) {            checkPoints[i] = sc.nextInt();        }        Arrays.sort(checkPoints);        if (n == 0 || n == 1) {            System.out.println(0);            ;        } else {            int minDist = Math.min(distance(checkPoints[0], checkPoints[n - 2], a), distance(checkPoints[1], checkPoints[n - 1], a));            System.out.println(minDist);        }    }    static int distance(int left,int right, int a){        if(a>=right){            return (a-left);        }        else if(a<=left){            return (right-a);        }        else {            return Math.min(a - left, right - a) + right - left;        }    }}
