import java.io.*;public class sol {    public static void main (String[] args) throws IOException {        BufferedReader br = new BufferedReader(new InputStreamReader(System.in));        int n = Integer.parseInt(br.readLine());        String a[] = new String[n];        for (int i = 0; i < n; i++) {            String line = br.readLine();            if (line.length() > 10) {                a[i] = "" + line.charAt(0) + (line.length() -2) + line.charAt(line.length() - 1);            }            else { a[i] = line;}        }        for (int i = 0; i < a.length; i++) {            System.out.println(a[i]);        }    }}
