import java.io.*;import java.util.*;import java.lang.*;import java.util.Random;public class Main {    public static void main (String[] args) throws java.lang.Exception {        Scanner scan = new Scanner (System.in);        int repeat = scan.nextInt();        while (repeat-->0) {            String test = scan.next();            int len = test.length();            if (len<=10) {                System.out.println(test);            }            else {                String val = ""+(test.length()-2);                String str = test.charAt(0)+val+test.charAt(test.length()-1);                System.out.println(str);            }        }    }}
