import java.util.*;public class Main{    public static void main (String[] args) {        Scanner sc=new Scanner(System.in);        int t=sc.nextInt();        int []arr=new int[t];        int []brr=new int[t];        for(int i=0;i<t;i++)        {            arr[i]=sc.nextInt();        }        for(int j=0;j<t-1;j++)        {            brr[j]=arr[j]+arr[j+1];        }        brr[t-1]=arr[t-1];        for(int x:brr)        {            System.out.print(x+" ");        }    }}
