import java.util.*;public class _714A {    public static void main(String[] args) {        Scanner sc = new Scanner(System.in);        long l1 = sc.nextLong();        long r1 = sc.nextLong();        long l2 = sc.nextLong();        long r2 = sc.nextLong();        long k = sc.nextLong();        if(r2<l1  || l2>r1)            System.out.println(0);        else{             boolean inc=k>=Math.max(l1,l2) &&  k<=Math.min(r1,r2);                 System.out.println(Math.min(r1,r2)-Math.max(l1,l2) + 1 - (inc ?1:0));        }    }}
