// package Mathematical; import java.io.BufferedReader;import java.io.IOException;import java.io.InputStreamReader;import java.math.BigInteger; public class PlusAndSquareRoot {    public static void main(String[] args)throws IOException {        BufferedReader br=new BufferedReader(new InputStreamReader(System.in));        int n=Integer.parseInt(br.readLine());        StringBuilder print=new StringBuilder();        BigInteger score=new BigInteger("2");        for(int i=1;i<=n;i++){            BigInteger reqd=BigInteger.valueOf(i).multiply(BigInteger.valueOf(i+1));            BigInteger square=reqd.multiply(reqd);            BigInteger add=square.subtract(score);            add=add.divide(BigInteger.valueOf(i));            print.append(add.toString()+"\n");            score=reqd;        }        System.out.println(print.toString());    }}
