import java.io.IOException;import java.util.*;public class VitayAgain {	public static void main(String[] args) throws IOException{		Scanner sc = new Scanner (System.in);		int n = sc.nextInt();		int arr [] = new int [n];		for (int i = 0 ; i  < arr.length ; i++) {			arr [i] = sc.nextInt();					}				if (arr[n-1] == 15) {			System.out.println("DOWN");			return;		}else if (arr[n-1] == 0) {			System.out.println("UP");			return;		}		if (n==1) {			System.out.println(-1);			return;		}				System.out.println(arr[arr.length -1]  > arr[arr.length-2]? "UP" : "DOWN");	}}
