import java.util.Scanner;public class _723B{    public static void main(String[] args)    {        Scanner sc = new Scanner(System.in);        int n=sc.nextInt();        char[] s = sc.next().toCharArray();        boolean in = false;        int len = 0, maxLen = 0, words = 0;        for (int i = 0; i < s.length; i++)        {            if ('a' <= s[i] && s[i] <= 'z' || 'A' <= s[i] && s[i] <= 'Z')                len++;            else                len = 0;            if (s[i] == '(')                in = true;            else if (s[i] == ')')                in = false;            if (in)            {                if (len == 1)                    words++;            }            else                maxLen = Math.max(len, maxLen);        }        System.out.println(maxLen + " " + words);    }}
