import java.util.HashMap;import java.util.Map;import java.util.Scanner;public class Main{    static Map<String,Integer> mp = new HashMap<>();    static Scanner in=new Scanner(System.in);    public static void main(String[] args) {        mp.put("monday",1);        mp.put("tuesday",2);        mp.put("wednesday",3);        mp.put("thursday",4);        mp.put("friday",5);        mp.put("saturday",6);        mp.put("sunday",7);        String a = in.next(),b = in.next();        int x = (mp.get(b) - mp.get(a) + 7) % 7;        if(x == 0 || x == 2 || x==3){            System.out.println("YES");        }else{            System.out.println("NO");        }    }}
