/* package codechef; // don't place package name! */import java.util.*;import java.lang.*;import java.io.*;public class Codechef{	public static void main (String[] args) throws java.lang.Exception{	   Scanner s=new Scanner(System.in);        int n=s.nextInt(),count=0;        String st=s.next();        for(int i=0;i<st.length();i++){            if(st.charAt(i)=='<')count++;            else break;        }        for(int i=st.length()-1;i>=0;i--){            if(st.charAt(i)=='>')count++;            else break;        }        System.out.println(count);	}}
