import java.util.Scanner;import java.util.Arrays;public class Grasshopper {public static void main(String[] args) {	Scanner scanner = new Scanner (System.in);	String s = scanner.next();	int n = s.length(),f=0;	int [] a;a=new int [n];	for (int i=0;i<n;i++) {		char x = s.charAt(i);		if (!(x=='A'||x=='E'||x=='Y'||x=='O'||x=='U' || x=='I')) a[f]++;		else f++;	}	Arrays.sort(a);	System.out.println(a[n-1] + 1);	scanner.close(); }}
