import java.util.*;public class Main{    public static void main(String args[])    {        Scanner sc =new Scanner(System.in);        int n=sc.nextInt();        int k=sc.nextInt();        sc.nextLine();        String str=sc.nextLine();        int a=str.indexOf('G');        int b=str.indexOf('T');        if(a<b)        {            for(int i=a;i<=b;i=i+k)            {                if(str.charAt(i)=='#')                {                    System.out.println("NO");                    System.exit(0);                }                else if(str.charAt(i)=='T')                {                    System.out.println("YES");                    System.exit(0);                }            }            System.out.println("NO");        }        else        {            for(int i=a;i>=b;i=i-k)            {                if(str.charAt(i)=='#')                {                    System.out.println("NO");                    System.exit(0);                }                else if(str.charAt(i)=='T')                {                    System.out.println("YES");                    System.exit(0);                }            }            System.out.println("NO");        }    }}                            
