# вводим кол-во вагонов, начальное положение зайца и контролераn, z1, k1 = map(int, input().split())z1 -= 1k1 -= 1 # вводим направлениеs = input() # направление контролераif (s == 'to head'):    napr = -1else:    napr = 1 # вводим состояние электричкиs = input() # условие проверки встречи зайца и контролераfor i in range(len(s)):    # если заяц и контролер в одном вагоне    if (k1 == z1):        print('Controller', i)        exit(0)        # если электричка движется    if (s[i] == '0'):        # если контролер ближе к хвосту и заяц не в голове, заяц ходит ближе к голове        if (k1 > z1 and z1 != 0):            z1 -= 1        # если заяц ближе к хвосту, но не в конце, заяц ходит ближе к хвосту        elif (k1 < z1 and z1 != n - 1):            z1 += 1        # шаг контролера        k1 += napr                # если контролер дошел до края поезда, поменять направление        if (k1 == 0 or k1 == n - 1):            napr = -napr    else:        # шаг контролера        k1 += napr         # если контролер дошел до края поезда, поменять направление        if (k1 == 0 or k1 == n - 1):            napr = -napr         # если контролер в голове, заяц в хвосте        if (k1 == 0):            z1 = n - 1        # если контролер в хвосте, заяц в голове        elif (k1 == n - 1):            z1 = 0        # если направление к хвосту и контролер не в голове, заяц в голове        elif (napr == 1 and k1 != 0):            z1 = 0        else:            z1 = n - 1    # если заяц и контролер в одном вагоне    if (k1 == z1):        print('Controller', i + 1)        exit(0) print('Stowaway') 
