import java.io.*;import java.lang.*;import java.util.*;  public class Solution{        public static void main(String[] args){                        Scanner scan = new Scanner(System.in);                int n= scan.nextInt();        int m = scan.nextInt();                int[] flowers = new int[n+1];                int[] p_s = new int[n+1];                int[] arr = new int[n+2];                for(int i=1;i<=n;i++)         flowers[i] = scan.nextInt();                    p_s[1] = flowers[1];                    for(int i=2;i<=n;i++)          p_s[i] = p_s[i-1]+flowers[i];                            int happiness = 0;                 while(m-->0){                        int l = scan.nextInt();            int r = scan.nextInt();                        if(p_s[r]>p_s[l-1]){                                arr[l] = arr[l]+1;                arr[r+1] = arr[r+1]-1;            }        }                for(int i=2;i<n+2;i++)         arr[i] = arr[i-1]+arr[i];                  int sum=0;                  for(int i=1;i<=n;i++){                          sum = sum+flowers[i]*arr[i];         }                  System.out.println(sum);                   }}
