# Aluno Rafael Pontes maxn = 1002 def criar_lista_com_zeros(n):    l = []    for i in range(n):        l.append(0)    return l def criar_lista_de_listas(n):    l = []    for i in range(n):        l.append([])    return l memoria = criar_lista_com_zeros(maxn)pesos = criar_lista_com_zeros(maxn)belezas = criar_lista_com_zeros(maxn)somas_pesos = criar_lista_com_zeros(maxn)somas_belezas = criar_lista_com_zeros(maxn)vt = criar_lista_de_listas(maxn)pr = criar_lista_com_zeros(maxn)n, m, w = 0, 0, 0 def encontrar(x):    global pr    if pr[x] == x:        return x    pr[x] = encontrar(pr[x])    return pr[x] def juntar(x, y):    f1 = encontrar(x)    f2 = encontrar(y)    if (f1 != f2):        pr[f1] = f2 # Inicio do programa # Leitura parametros iniciaisn, m, w = [int(x) for x in input().split()] # Inicizalizacao das principais variaveisfor i in range(maxn):    memoria[i] = 0    pr[i] = i    somas_pesos[i] = 0    somas_belezas[i] = 0 # Leitura dos pesos e belezas# Adiciono um elemento para o indice começar em 1pesos = [0] + [int(x) for x in input().split()]belezas = [0] + [int(x) for x in input().split()] for i in range(1, m + 1):    x, y = [int(x) for x in input().split()]    juntar(x,y) for i in range(1, n + 1):    x = encontrar(i)    vt[x].append((pesos[i],belezas[i]))    somas_pesos[x] += pesos[i]    somas_belezas[x] += belezas[i] for i in range(1, n + 1):    if (len(vt[i]) == 0): continue    vt[i].append((somas_pesos[i],somas_belezas[i])) for i in range(1, n + 1):    if (len(vt[i]) == 0): continue    for j in range(w, -1, -1):        for k in range(0, len(vt[i])):            W = vt[i][k][0]            B = vt[i][k][1]            if (j - W >= 0):                memoria[j] = max(memoria[j], memoria[j-W] + B) print("%d" % memoria[w])
