// package b0808; import java.util.ArrayList;import java.util.List;import java.util.Scanner; /** * * @author Admin */public class C {    static final int INF = Integer.MAX_VALUE;        public static void main(String[] args) {        Scanner in = new Scanner(System.in);        int n = in.nextInt();        int[][][] value = new int[10][2][n+1];        List<Integer> l = new ArrayList<>();        for (int bitpos = 0; bitpos < value.length; ++bitpos)            for (int bitval = 0; bitval < 2; ++bitval) {                l.clear();                for (int i = 1; i <= n; ++i) {                    if ((1 & (i >> bitpos)) == bitval) l.add(i);                }                if (l.isEmpty()) {                    for (int i = 1; i <= n; ++i)                        value[bitpos][bitval][i] = INF;                } else {                    System.out.println(l.size());                    for (int i : l) System.out.print(i + " ");                    System.out.println();                    for (int i = 1; i <= n; ++i)                         value[bitpos][bitval][i] = in.nextInt();                }            }        System.out.println(-1);        for (int i = 1; i <= n; ++i) {            int result = INF;            for (int bitpos = 0; bitpos < value.length; ++bitpos)                result = Math.min(result, value[bitpos][1 & ~(i >> bitpos)][i]);            System.out.print(result + " ");        }    }}   				 	 			    			 		 	 	 		
