import java.util.*;import java.io.*;import java.math.*;/** * * @author alanl */public class Main{    static BufferedReader input = new BufferedReader(new InputStreamReader(System.in));    static StringTokenizer st;    static PrintWriter pr = new PrintWriter(new BufferedWriter(new OutputStreamWriter(System.out)));    static ArrayList<Integer>arr = new ArrayList(), ans = new ArrayList();    public static void main(String[] args) throws IOException{        int n = readInt(), m = readInt();        boolean flag = true;        int x = Integer.MAX_VALUE, y = Integer.MAX_VALUE, x1 = -1, y1 = -1, cnt = 0;        char[][]adj = new char[n][m];        for(int i = 0; i<n; i++){            String s = readLine();            for(int j = 0; j<m; j++){                adj[i][j] = s.charAt(j);                if(s.charAt(j)=='X'){                    cnt++;                    if(i<=x && j<=y){                        x = i;                        y = j;                    }                    if(i>=x1 && j>=y1){                        x1 = i;                        y1 = j;                    }                }            }        }        if(cnt!=(Math.abs(x-x1)+1)*(Math.abs(y-y1)+1)){            println("NO");            return;        }        for(int i = x; i<=x1; i++){            for(int j = y; j<=y1; j++){                if(adj[i][j]!='X'){                    flag = false;                    break;                }            }        }        if(!flag)println("NO");        else println("YES");    }    static String next () throws IOException {        while (st == null || !st.hasMoreTokens())                st = new StringTokenizer(input.readLine().trim());        return st.nextToken();    }    static long readLong () throws IOException {        return Long.parseLong(next());    }    static int readInt () throws IOException {        return Integer.parseInt(next());    }    static double readDouble () throws IOException {        return Double.parseDouble(next());    }    static char readChar () throws IOException {        return next().charAt(0);    }    static String readLine () throws IOException {        return input.readLine().trim();    }    static void print(Object b) {        System.out.print(b);    }    static void println(Object b) {        System.out.println(b);    }    static void println() {        System.out.println();    }}
