import java.util.*;public class Solution{    public static void main(String[] args){        Scanner s=new Scanner(System.in);        int n=s.nextInt();        s.nextLine();        String line=s.nextLine();        char a[]=new char[n];        int index=n/2;int i=1;int j=1;                if(n%2!=0){            a[index]=line.charAt(0);            while(i<n){                a[index-j]=line.charAt(i);                i++;                if(i==n)                    break;                a[index+j]=line.charAt(i);                i++;                if(i==n)                    break;                j++;            }        }        else{            index--;            a[index]=line.charAt(0);            while(i<n){                a[index+j]=line.charAt(i);                i++;                if(i==n)                    break;                a[index-j]=line.charAt(i);                i++;                if(i==n)                    break;                j++;            }                    }        String result=String.valueOf(a);        System.out.println(result);                    }} 
