//JDopeimport java.util.*;import java.io.*;import java.math.*;public class B{public static void main(String[] omkar) throws Exception{   BufferedReader in = new BufferedReader(new InputStreamReader(System.in));   StringTokenizer st = new StringTokenizer(in.readLine());   StringBuilder sb = new StringBuilder();   int x1 = Integer.parseInt(st.nextToken());   int y1 = Integer.parseInt(st.nextToken());   st = new StringTokenizer(in.readLine());   int x2 = Integer.parseInt(st.nextToken());   int y2 = Integer.parseInt(st.nextToken());   st = new StringTokenizer(in.readLine());   int x3 = Integer.parseInt(st.nextToken());   int y3 = Integer.parseInt(st.nextToken());   double m1, m2;   int tot = 0;   sb.append("3\n");   sb.append((-x1+x2+x3)+" "+(-y1+y2+y3)+"\n");   sb.append((x1-x2+x3)+" "+(y1-y2+y3)+"\n");   sb.append((x1+x2-x3)+" "+(y1+y2-y3));   System.out.println(sb);           } public static int[] readArr(int N, BufferedReader in, StringTokenizer st) throws Exception      {         int[] arr = new int[N];         st = new StringTokenizer(in.readLine());         for(int i=0; i < N; i++)            arr[i] = Integer.parseInt(st.nextToken());         return arr;      }  }
