# url: https://codeforces.com/contest/749/problem/b# tag:# difficulty:from typing import List INF = float("inf")NINF = float("-inf")  def read_string():    return input()  def read_string_line():    return [x for x in input().split(" ")]  def read_int_line():    return [int(x) for x in input().split(" ")]  def read_int():    return int(input())  def get_int_arr(s):    return [int(x) for x in s.split()]  def get_sum(acc, l, r):    """     l, r are original index     """    return acc[r] - acc[l]  P = int(1e9 + 7)  def exgcd(x, y):    if y == 0:        return x, 1, 0    g, a, b = exgcd(y, x % y)    t = x // y    return g, b, a - t * b  def inv(x):    g, a, b = exgcd(x, P)    return a  def calc_acc(arr):    ans = [0]    for a in arr:        ans.append(a + ans[-1])    return ans  lines: List[str] = [*open(0)]x0, x1, x2 = [get_int_arr(s) for s in lines[:3]]  def solve(p0, p1, p2):    print("%d %d" % (p0[0] + p1[0] - p2[0], p0[1] + p1[1] - p2[1]))  print(3)solve(x0, x1, x2)solve(x2, x1, x0)solve(x0, x2, x1)
